        "from": {"type": "integer"},
        "last_page": {"type": "integer"},
        "last_page_url": {"type": "string"},
        "links": {
          "type": "array",
          "items": {
            "type": "object",
            "properties": {
              "url": {"type": ["string", "null"]},
              "label": {"type": "string"},
